glsl3.vert:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

#if CC_USE_BATCHING
  in float a_dyn_batch_id;
  uniform CCLocalBatched {
    highp mat4 cc_matWorlds[10];
  };
#else
uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
};

#endif

void CCGetWorldMatrix (out highp mat4 matWorld) {
  #if CC_USE_BATCHING
    matWorld = cc_matWorlds[int(a_dyn_batch_id + 0.5)];
  #else
    matWorld = cc_matWorld;
  #endif
}

void CCGetWorldMatrixFull (out highp mat4 matWorld, out highp mat4 matWorldIT) {
  #if CC_USE_BATCHING
    matWorld = cc_matWorlds[int(a_dyn_batch_id + 0.5)];
    matWorldIT = matWorld;
  #else
    matWorld = cc_matWorld;
    matWorldIT = cc_matWorldIT;
  #endif
}

struct StandardAttributes {
  vec4 position;
  vec3 normal;
  vec4 tangent;
  int index;
};

in vec3 a_position;
in vec3 a_normal;
in vec4 a_tangent;

void CCDecode (out StandardAttributes attr) {
  attr.position = vec4(a_position, 1.0);
  attr.normal = a_normal;
  attr.tangent = a_tangent;
}

#if CC_USE_SKINNING

in vec4 a_weights;
in vec4 a_joints;
uniform CCSkinningTexture {
  highp vec4 cc_jointsTextureInfo;
};
uniform CCSkinningAnimation {
  highp vec4 cc_jointsAnimInfo;
};
uniform sampler2D cc_jointsTexture;

#if CC_USE_SKINNING == 1
  highp float decode32(highp vec4 rgba) {
    rgba = rgba * 255.0;
    highp float Sign = 1.0 - step(128.0, rgba[3]) * 2.0;
    highp float Exponent = 2.0 * mod(rgba[3], 128.0) + step(128.0, rgba[2]) - 127.0;
    highp float Mantissa = mod(rgba[2], 128.0) * 65536.0 + rgba[1] * 256.0 + rgba[0] + 8388608.0;
    return Sign * exp2(Exponent - 23.0) * Mantissa;
  }
#endif

#if CC_USE_SKINNING == 1
  void getJointDQ(float i, out vec4 Qr, out vec4 Qt, out vec3 S) {
    highp float j = 12.0 * (cc_jointsAnimInfo.x * i + cc_jointsAnimInfo.y) + cc_jointsTextureInfo.z;
    highp float invSize = cc_jointsTextureInfo.y;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointsTextureInfo.x;
    y = (y + 0.5) * invSize;
    Qr = vec4(
      decode32(texture(cc_jointsTexture, vec2((x + 0.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 1.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 2.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 3.5) * invSize, y)))
    );
    Qt = vec4(
      decode32(texture(cc_jointsTexture, vec2((x + 4.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 5.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 6.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 7.5) * invSize, y)))
    );
    S = vec3(
      decode32(texture(cc_jointsTexture, vec2((x + 8.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 9.5) * invSize, y))),
      decode32(texture(cc_jointsTexture, vec2((x + 10.5) * invSize, y)))
    );
  }
#elif CC_USE_SKINNING == 2
  void getJointDQ(float i, out vec4 Qr, out vec4 Qt, out vec3 S) {
    highp float j = 3.0 * (cc_jointsAnimInfo.x * i + cc_jointsAnimInfo.y) + cc_jointsTextureInfo.z;
    highp float invSize = cc_jointsTextureInfo.y;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointsTextureInfo.x;
    y = (y + 0.5) * invSize;
    Qr = texture(cc_jointsTexture, vec2((x + 0.5) * invSize, y));
    Qt = texture(cc_jointsTexture, vec2((x + 1.5) * invSize, y));
    S = texture(cc_jointsTexture, vec2((x + 2.5) * invSize, y)).xyz;
  }
#endif
void skinRTS(out vec4 R, out vec3 T, out vec3 S) {
  vec4 r, t, Qt = vec4(0.0); vec3 s;
  R = vec4(0.0); S = vec3(0.0);

  for (int i = 0; i < 4; i++) {
    float w = a_weights[i];
    getJointDQ(a_joints[i], r, t, s);
    S += s * w; R += r * w; Qt += t * w;
  }

  float invNorm = 1.0 / length(R); R *= invNorm; Qt *= invNorm;
  T = 2.0 * (R.w * Qt.xyz - Qt.w * R.xyz + cross(R.xyz, Qt.xyz));
}

vec3 VectorTransformQuat(vec3 v, vec4 Q) {
  return v + 2.0 * cross(Q.xyz, cross(Q.xyz, v) + Q.w * v);
}

void CCSkin(inout vec4 position) {
  vec4 R; vec3 T, S;
  skinRTS(R, T, S);
  position.xyz = VectorTransformQuat(position.xyz * S, R) + T;
}

void CCSkin(inout StandardAttributes attr) {
  vec4 R; vec3 T, S;
  skinRTS(R, T, S);
  attr.position.xyz = VectorTransformQuat(attr.position.xyz * S, R) + T;
  attr.normal = VectorTransformQuat(attr.normal, R);
  attr.tangent.xyz = VectorTransformQuat(attr.tangent.xyz, R);
}

#endif

struct StandardVertInput {
  highp vec4 position;
  vec3 normal;
  vec4 tangent;
  int index;
};

void CCAttrToInput (in StandardAttributes attr, out StandardVertInput In) {
  In.position = attr.position;
  In.normal = attr.normal;
  In.tangent = attr.tangent;
  In.index = attr.index;
}

void CCVertInput (out StandardVertInput In) {
  StandardAttributes attr;
  CCDecode(attr);
  #if CC_USE_SKINNING
    CCSkin(attr);
  #endif
  CCAttrToInput(attr, In);
}

uniform Constants {
  vec4 tilingOffset;
  vec4 albedo;
  vec4 albedoScale;
  vec4 pbrParams;
  vec4 pbrScale;
  vec4 emissive;
  vec4 emissiveScale;
};

#if USE_VERTEX_COLOR
  in vec3 a_color;
  out vec3 v_color;
#endif

out vec3 v_position;
out vec3 v_normal;
#if USE_NORMAL_MAP
  out vec3 v_tangent;
  out vec3 v_bitangent;
#endif

#if USE_ALBEDO_MAP || USE_NORMAL_MAP || USE_PBR_MAP || USE_EMISSIVE_MAP || USE_METALLIC_ROUGHNESS_MAP || USE_OCCLUSION_MAP
  in vec2 a_texCoord;
  out vec2 v_uv;
#endif

highp vec4 vert () {
  StandardVertInput In;
  CCVertInput(In);

  highp mat4 matWorld, matWorldIT;
  CCGetWorldMatrixFull(matWorld, matWorldIT);

  highp vec4 pos = matWorld * In.position;

  v_position = pos.xyz;
  v_normal = normalize((matWorldIT * vec4(In.normal, 0.0)).xyz);
  #if USE_NORMAL_MAP
    v_tangent = normalize((matWorldIT * vec4(In.tangent.xyz, 0.0)).xyz);
    v_bitangent = cross(v_normal, v_tangent) * In.tangent.w;
  #endif

  #if USE_ALBEDO_MAP || USE_NORMAL_MAP || USE_PBR_MAP || USE_EMISSIVE_MAP || USE_METALLIC_ROUGHNESS_MAP || USE_OCCLUSION_MAP
    v_uv = a_texCoord * tilingOffset.xy + tilingOffset.zw;
  #endif

  #if USE_VERTEX_COLOR
    v_color = a_color;
  #endif

  return cc_matProj * (cc_matView * matWorld) * In.position;
}

void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};

#if CC_USE_IBL
uniform samplerCube cc_environment;

vec3 unpackNormal(vec4 nmap) {
  return nmap.xyz * 2.0 - 1.0;
}

vec3 unpackRGBE(vec4 rgbe) {
    return rgbe.rgb * pow(2.0, rgbe.a * 255.0 - 128.0);
}
#endif

#define saturate(a) clamp( a, 0.0, 1.0 )

vec3 SRGBToLinear(vec3 gamma)
{
	return pow(gamma, vec3(2.2));
}

vec3 LinearToSRGB(vec3 linear)
{
	return pow(linear, vec3(0.454545));
}
uniform CCForwardLight {
  highp vec4 cc_sphereLitPos[2];
  vec4 cc_sphereLitSizeRange[2];
  vec4 cc_sphereLitColor[2];
  highp vec4 cc_spotLitPos[2];
  vec4 cc_spotLitSizeRangeAngle[2];
  vec4 cc_spotLitDir[2];
  vec4 cc_spotLitColor[2];
};

float SmoothDistAtt2(float distSqr, float invSqrAttRadius) {
  float factor = distSqr * invSqrAttRadius;
  float factor2 = factor * factor;
  float factor3 = factor2 * factor2;
  float smoothFactor = clamp(1.0 - factor3 * factor3, 0.0, 1.0);
  return smoothFactor * smoothFactor;
}

float SmoothDistAtt(float distSqr, float invSqrAttRadius) {
  float factor = distSqr * invSqrAttRadius;
  float smoothFactor = clamp(1.0 - factor * factor, 0.0, 1.0);
  return smoothFactor * smoothFactor;
}

float GetDistAtt(float distSqr, float invSqrAttRadius) {
  float attenuation = 1.0 / max(distSqr, 0.01*0.01);
  attenuation *= SmoothDistAtt(distSqr , invSqrAttRadius);
  return attenuation;
}

float GetAngleAtt(vec3 L, vec3 litDir, float litAngleScale, float litAngleOffset) {
  float cd = dot(litDir, L);
  float attenuation = clamp(cd * litAngleScale + litAngleOffset, 0.0, 1.0);
  return (attenuation * attenuation);
}

float GGXMobile(float roughness, float NoH, vec3 H, vec3 N) {
  vec3 NxH = cross(N, H);
  float OneMinusNoHSqr = dot(NxH, NxH);
  float a = roughness * roughness;
  float n = NoH * a;
  float p = a / (OneMinusNoHSqr + n * n);
  return p * p;
}

float CalcSpecular(float roughness, float NoH, vec3 H, vec3 N) {
  return (roughness*0.25 + 0.25) * GGXMobile(roughness, NoH, H, N);
}
vec3 BRDFApprox(vec3 specular, float roughness, float NoV) {
  const vec4 c0 = vec4(-1.0, -0.0275, -0.572, 0.022);
  const vec4 c1 = vec4(1.0, 0.0425, 1.04, -0.04);
  vec4 r = roughness * c0 + c1;
  float a004 = min( r.x * r.x, exp2( -9.28 * NoV ) ) * r.x + r.y;
  vec2 AB = vec2( -1.04, 1.04 ) * a004 + r.zw;
  AB.y *= clamp(50.0 * specular.g, 0.0, 1.0);
  return specular * AB.x + AB.y;
}

vec3 CalcDynamicLighting(vec3 worldPos, vec3 N, vec3 V, vec3 diffuse, vec3 specular, float roughness) {
  vec3 lighting = vec3(0.0);

  vec3 diffuseContrib = diffuse / 3.14159265359;
  for (int i = 0; i < 2; i++) {
    vec3 PLU = cc_sphereLitPos[i].xyz - worldPos;
    vec3 PL = normalize(PLU);
    vec3 PH = normalize(PL + V);
    float PNL = max(dot(N, PL), 0.001);
    float PNH = max(dot(N, PH), 0.0);

    float distSqr = dot(PLU, PLU);
    float litRadius = cc_sphereLitSizeRange[i].x;
    float litRadiusSqr = litRadius * litRadius;
    float illum = 3.14159265359 * (litRadiusSqr / max(litRadiusSqr , distSqr));
    float attRadiusSqrInv = 1.0 / max(cc_sphereLitSizeRange[i].y, 0.01);
    attRadiusSqrInv *= attRadiusSqrInv;
    float att = GetDistAtt(distSqr, attRadiusSqrInv);
    vec3 lspec = specular * CalcSpecular(roughness, PNH, PH, N);
    lighting += PNL * cc_sphereLitColor[i].rgb * cc_sphereLitColor[i].w * illum * att * (diffuseContrib + lspec);
  }
  for (int i = 0; i < 2; i++) {
    vec3 SLU = cc_spotLitPos[i].xyz - worldPos;
    vec3 SL = normalize(SLU);
    vec3 SH = normalize(SL + V);
    float SNL = max(dot(N, SL), 0.001);
    float SNH = max(dot(N, SH), 0.0);

    float distSqr = dot(SLU, SLU);
    float litRadius = cc_spotLitSizeRangeAngle[i].x;
    float litRadiusSqr = litRadius * litRadius;
    float illum = 3.14159265359 * (litRadiusSqr / max(litRadiusSqr , distSqr));
    float attRadiusSqrInv = 1.0 / max(cc_spotLitSizeRangeAngle[i].y, 0.01);
    attRadiusSqrInv *= attRadiusSqrInv;

    float cosInner = max(dot(-cc_spotLitDir[i].xyz, SL), 0.01);
    float cosOuter = cc_spotLitSizeRangeAngle[i].z;
    float litAngleScale = 1.0 / max(0.001, cosInner - cosOuter);
    float litAngleOffset = -cosOuter * litAngleScale;
    float att = GetDistAtt(distSqr, attRadiusSqrInv);
    att *= GetAngleAtt(SL, -cc_spotLitDir[i].xyz, litAngleScale, litAngleOffset);

    vec3 lspec = specular * CalcSpecular(roughness, SNH, SH, N);
    lighting += SNL * cc_spotLitColor[i].rgb * cc_spotLitColor[i].w * illum * att * (diffuseContrib + lspec);
  }

  return lighting;
}

struct StandardSurface {
  vec4 albedo;
  vec3 position;
  vec3 normal;
  vec3 emissive;
  float roughness;
  float metallic;
  float occlusion;
};

vec4 CCStandardShading (StandardSurface s) {
  vec3 diffuse = s.albedo.rgb * (1.0 - s.metallic);
  vec3 specular = mix(vec3(0.04), s.albedo.rgb, s.metallic);

  vec3 N = normalize(s.normal);
  vec3 V = normalize(cc_cameraPos.xyz - s.position);
  vec3 L = normalize(-cc_mainLitDir.xyz);
  vec3 H = normalize(L+V);
  float NV = max(abs(dot(N, V)), 0.001);
  float NL = max(dot(N, L), 0.001);
  float NH = max(dot(N, H), 0.0);

  specular = BRDFApprox(specular, s.roughness, NV);

  vec3 diffuseContrib = diffuse / 3.14159265359;
  vec3 specularContrib = specular * CalcSpecular(s.roughness, NH, H, N);

  vec3 finalColor = NL * cc_mainLitColor.rgb * cc_mainLitColor.w * (diffuseContrib + specularContrib);
  finalColor += CalcDynamicLighting(s.position, N, V, diffuse, specular, s.roughness);

  float fAmb = 0.5 - N.y * 0.5;
  vec3 ambDiff = mix(cc_ambientSky.rgb, cc_ambientGround.rgb, fAmb) * cc_ambientSky.w;
  finalColor += (ambDiff.rgb * diffuse);

  #if CC_USE_IBL
    vec3 R = normalize(reflect(-V, N));

    vec4 envmap = textureLod(cc_environment, R, s.roughness * cc_ambientGround.w);

    #if CC_USE_IBL == 2
      vec3 env = unpackRGBE(envmap);
    #else
      vec3 env = SRGBToLinear(envmap.rgb) * cc_ambientSky.w;
    #endif
    finalColor += env * specular;
  #endif

  finalColor = finalColor * s.occlusion;

  #if CC_USE_HDR
    s.emissive *= cc_exposure.w;
  #endif
  finalColor += s.emissive;

  return vec4(finalColor, s.albedo.a);
}

vec3 ACESToneMap(vec3 color) {
    const float A = 2.51;
    const float B = 0.03;
    const float C = 2.43;
    const float D = 0.59;
    const float E = 0.14;
    return (color * (A * color + B)) / (color * (C * color + D) + E);
}

vec4 CCFragOutput (vec4 color) {
  #if !CC_USE_HDR
    color.rgb = ACESToneMap(color.rgb);
    color.rgb = LinearToSRGB(color.rgb);
  #endif
  return color;
}

uniform Constants {
  vec4 tilingOffset;
  vec4 albedo;
  vec4 albedoScale;
  vec4 pbrParams;
  vec4 pbrScale;
  vec4 emissive;
  vec4 emissiveScale;
};

in vec3 v_position;

#if USE_ALBEDO_MAP || USE_NORMAL_MAP || USE_PBR_MAP || USE_EMISSIVE_MAP || USE_METALLIC_ROUGHNESS_MAP || USE_OCCLUSION_MAP
  in vec2 v_uv;
#endif

#if USE_VERTEX_COLOR
  in vec3 v_color;
#endif

in vec3 v_normal;
#if USE_NORMAL_MAP
  in vec3 v_tangent;
  in vec3 v_bitangent;
#endif

#if USE_ALBEDO_MAP
  uniform sampler2D albedoMap;
#endif
#if USE_NORMAL_MAP
  uniform sampler2D normalMap;
#endif
#if USE_PBR_MAP
  uniform sampler2D pbrMap;
#endif
#if USE_METALLIC_ROUGHNESS_MAP
  uniform sampler2D metallicRoughnessMap;
#endif
#if USE_OCCLUSION_MAP
  uniform sampler2D occlusionMap;
#endif
#if USE_EMISSIVE_MAP
  uniform sampler2D emissiveMap;
#endif
#if USE_ALPHA_TEST
#endif

void surf (out StandardSurface s) {
  vec4 baseColor = albedo;
  #if USE_VERTEX_COLOR
    baseColor.rgb *= v_color;
  #endif
  #if USE_ALBEDO_MAP
    vec4 texColor = texture(albedoMap, v_uv);
    texColor.rgb = SRGBToLinear(texColor.rgb);
    baseColor *= texColor;
  #endif
  s.albedo = baseColor;
  s.albedo.rgb *= albedoScale.xyz;

  #if USE_ALPHA_TEST
    if (s.albedo.ALPHA_TEST_CHANNEL < albedoScale.w) discard;
  #endif

  s.normal = v_normal;
  #if USE_NORMAL_MAP
    vec3 nmmp = texture(normalMap, v_uv).xyz - vec3(0.5);
    s.normal =
      (nmmp.x * pbrScale.w) * normalize(v_tangent) +
      (nmmp.y * pbrScale.w) * normalize(v_bitangent) +
      nmmp.z * normalize(s.normal);
  #endif

  s.position = v_position;

  vec4 pbr = pbrParams;
  #if USE_PBR_MAP
    pbr = texture(pbrMap, v_uv);
  #endif
  #if USE_METALLIC_ROUGHNESS_MAP
    vec4 metallicRoughness = texture(metallicRoughnessMap, v_uv);
    pbr.METALLIC_CHANNEL = metallicRoughness.METALLIC_CHANNEL;
    pbr.ROUGHNESS_CHANNEL = metallicRoughness.ROUGHNESS_CHANNEL;
  #endif
  #if USE_OCCLUSION_MAP
    pbr.OCCLUSION_CHANNEL = texture(occlusionMap, v_uv).OCCLUSION_CHANNEL;
  #endif
  pbr *= pbrScale;
  s.roughness = clamp(pbr.ROUGHNESS_CHANNEL, 0.04, 1.0);
  s.metallic = clamp(pbr.METALLIC_CHANNEL, 0.0, 0.96);
  s.occlusion = pbr.OCCLUSION_CHANNEL;

  s.emissive = emissive.rgb * emissiveScale.xyz;
  #if USE_EMISSIVE_MAP
    s.emissive *= SRGBToLinear(texture(emissiveMap, v_uv).rgb);
  #endif
}

vec4 frag () {
  StandardSurface s; surf(s);
  vec4 color = CCStandardShading(s);
  return CCFragOutput(color);
}

out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

glsl1.vert:
precision highp float;
uniform mat4 cc_matView;
uniform mat4 cc_matProj;

#if CC_USE_BATCHING
  attribute float a_dyn_batch_id;
  uniform highp mat4 cc_matWorlds[10];

#else
uniform highp mat4 cc_matWorld;
uniform highp mat4 cc_matWorldIT;

#endif

void CCGetWorldMatrix (out highp mat4 matWorld) {
  #if CC_USE_BATCHING
    matWorld = cc_matWorlds[int(a_dyn_batch_id + 0.5)];
  #else
    matWorld = cc_matWorld;
  #endif
}

void CCGetWorldMatrixFull (out highp mat4 matWorld, out highp mat4 matWorldIT) {
  #if CC_USE_BATCHING
    matWorld = cc_matWorlds[int(a_dyn_batch_id + 0.5)];
    matWorldIT = matWorld;
  #else
    matWorld = cc_matWorld;
    matWorldIT = cc_matWorldIT;
  #endif
}

struct StandardAttributes {
  vec4 position;
  vec3 normal;
  vec4 tangent;
  int index;
};

attribute vec3 a_position;
attribute vec3 a_normal;
attribute vec4 a_tangent;

void CCDecode (out StandardAttributes attr) {
  attr.position = vec4(a_position, 1.0);
  attr.normal = a_normal;
  attr.tangent = a_tangent;
}

#if CC_USE_SKINNING

attribute vec4 a_weights;
attribute vec4 a_joints;
uniform highp vec4 cc_jointsTextureInfo;
uniform highp vec4 cc_jointsAnimInfo;
uniform sampler2D cc_jointsTexture;

#if CC_USE_SKINNING == 1
  highp float decode32(highp vec4 rgba) {
    rgba = rgba * 255.0;
    highp float Sign = 1.0 - step(128.0, rgba[3]) * 2.0;
    highp float Exponent = 2.0 * mod(rgba[3], 128.0) + step(128.0, rgba[2]) - 127.0;
    highp float Mantissa = mod(rgba[2], 128.0) * 65536.0 + rgba[1] * 256.0 + rgba[0] + 8388608.0;
    return Sign * exp2(Exponent - 23.0) * Mantissa;
  }
#endif

#if CC_USE_SKINNING == 1
  void getJointDQ(float i, out vec4 Qr, out vec4 Qt, out vec3 S) {
    highp float j = 12.0 * (cc_jointsAnimInfo.x * i + cc_jointsAnimInfo.y) + cc_jointsTextureInfo.z;
    highp float invSize = cc_jointsTextureInfo.y;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointsTextureInfo.x;
    y = (y + 0.5) * invSize;
    Qr = vec4(
      decode32(texture2D(cc_jointsTexture, vec2((x + 0.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 1.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 2.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 3.5) * invSize, y)))
    );
    Qt = vec4(
      decode32(texture2D(cc_jointsTexture, vec2((x + 4.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 5.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 6.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 7.5) * invSize, y)))
    );
    S = vec3(
      decode32(texture2D(cc_jointsTexture, vec2((x + 8.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 9.5) * invSize, y))),
      decode32(texture2D(cc_jointsTexture, vec2((x + 10.5) * invSize, y)))
    );
  }
#elif CC_USE_SKINNING == 2
  void getJointDQ(float i, out vec4 Qr, out vec4 Qt, out vec3 S) {
    highp float j = 3.0 * (cc_jointsAnimInfo.x * i + cc_jointsAnimInfo.y) + cc_jointsTextureInfo.z;
    highp float invSize = cc_jointsTextureInfo.y;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointsTextureInfo.x;
    y = (y + 0.5) * invSize;
    Qr = texture2D(cc_jointsTexture, vec2((x + 0.5) * invSize, y));
    Qt = texture2D(cc_jointsTexture, vec2((x + 1.5) * invSize, y));
    S = texture2D(cc_jointsTexture, vec2((x + 2.5) * invSize, y)).xyz;
  }
#endif
void skinRTS(out vec4 R, out vec3 T, out vec3 S) {
  vec4 r, t, Qt = vec4(0.0); vec3 s;
  R = vec4(0.0); S = vec3(0.0);

  for (int i = 0; i < 4; i++) {
    float w = a_weights[i];
    getJointDQ(a_joints[i], r, t, s);
    S += s * w; R += r * w; Qt += t * w;
  }

  float invNorm = 1.0 / length(R); R *= invNorm; Qt *= invNorm;
  T = 2.0 * (R.w * Qt.xyz - Qt.w * R.xyz + cross(R.xyz, Qt.xyz));
}

vec3 VectorTransformQuat(vec3 v, vec4 Q) {
  return v + 2.0 * cross(Q.xyz, cross(Q.xyz, v) + Q.w * v);
}

void CCSkin(inout vec4 position) {
  vec4 R; vec3 T, S;
  skinRTS(R, T, S);
  position.xyz = VectorTransformQuat(position.xyz * S, R) + T;
}

void CCSkin(inout StandardAttributes attr) {
  vec4 R; vec3 T, S;
  skinRTS(R, T, S);
  attr.position.xyz = VectorTransformQuat(attr.position.xyz * S, R) + T;
  attr.normal = VectorTransformQuat(attr.normal, R);
  attr.tangent.xyz = VectorTransformQuat(attr.tangent.xyz, R);
}

#endif

struct StandardVertInput {
  highp vec4 position;
  vec3 normal;
  vec4 tangent;
  int index;
};

void CCAttrToInput (in StandardAttributes attr, out StandardVertInput In) {
  In.position = attr.position;
  In.normal = attr.normal;
  In.tangent = attr.tangent;
  In.index = attr.index;
}

void CCVertInput (out StandardVertInput In) {
  StandardAttributes attr;
  CCDecode(attr);
  #if CC_USE_SKINNING
    CCSkin(attr);
  #endif
  CCAttrToInput(attr, In);
}

uniform vec4 tilingOffset;

#if USE_VERTEX_COLOR
  attribute vec3 a_color;
  varying vec3 v_color;
#endif

varying vec3 v_position;
varying vec3 v_normal;
#if USE_NORMAL_MAP
  varying vec3 v_tangent;
  varying vec3 v_bitangent;
#endif

#if USE_ALBEDO_MAP || USE_NORMAL_MAP || USE_PBR_MAP || USE_EMISSIVE_MAP || USE_METALLIC_ROUGHNESS_MAP || USE_OCCLUSION_MAP
  attribute vec2 a_texCoord;
  varying vec2 v_uv;
#endif

highp vec4 vert () {
  StandardVertInput In;
  CCVertInput(In);

  highp mat4 matWorld, matWorldIT;
  CCGetWorldMatrixFull(matWorld, matWorldIT);

  highp vec4 pos = matWorld * In.position;

  v_position = pos.xyz;
  v_normal = normalize((matWorldIT * vec4(In.normal, 0.0)).xyz);
  #if USE_NORMAL_MAP
    v_tangent = normalize((matWorldIT * vec4(In.tangent.xyz, 0.0)).xyz);
    v_bitangent = cross(v_normal, v_tangent) * In.tangent.w;
  #endif

  #if USE_ALBEDO_MAP || USE_NORMAL_MAP || USE_PBR_MAP || USE_EMISSIVE_MAP || USE_METALLIC_ROUGHNESS_MAP || USE_OCCLUSION_MAP
    v_uv = a_texCoord * tilingOffset.xy + tilingOffset.zw;
  #endif

  #if USE_VERTEX_COLOR
    v_color = a_color;
  #endif

  return cc_matProj * (cc_matView * matWorld) * In.position;
}

void main() { gl_Position = vert(); }

glsl1.frag:
#if CC_USE_IBL
  #ifdef GL_EXT_shader_texture_lod
    #extension GL_EXT_shader_texture_lod : enable
  #endif
#endif

precision highp float;
uniform vec4 cc_cameraPos;
uniform vec4 cc_exposure;
uniform vec4 cc_mainLitDir;
uniform vec4 cc_mainLitColor;
uniform vec4 cc_ambientSky;
uniform vec4 cc_ambientGround;

#if CC_USE_IBL
uniform samplerCube cc_environment;

vec3 unpackNormal(vec4 nmap) {
  return nmap.xyz * 2.0 - 1.0;
}

vec3 unpackRGBE(vec4 rgbe) {
    return rgbe.rgb * pow(2.0, rgbe.a * 255.0 - 128.0);
}
#endif

#define saturate(a) clamp( a, 0.0, 1.0 )

vec3 SRGBToLinear(vec3 gamma)
{
	return pow(gamma, vec3(2.2));
}

vec3 LinearToSRGB(vec3 linear)
{
	return pow(linear, vec3(0.454545));
}
uniform highp vec4 cc_sphereLitPos[2];
uniform vec4 cc_sphereLitSizeRange[2];
uniform vec4 cc_sphereLitColor[2];
uniform highp vec4 cc_spotLitPos[2];
uniform vec4 cc_spotLitSizeRangeAngle[2];
uniform vec4 cc_spotLitDir[2];
uniform vec4 cc_spotLitColor[2];

float SmoothDistAtt2(float distSqr, float invSqrAttRadius) {
  float factor = distSqr * invSqrAttRadius;
  float factor2 = factor * factor;
  float factor3 = factor2 * factor2;
  float smoothFactor = clamp(1.0 - factor3 * factor3, 0.0, 1.0);
  return smoothFactor * smoothFactor;
}

float SmoothDistAtt(float distSqr, float invSqrAttRadius) {
  float factor = distSqr * invSqrAttRadius;
  float smoothFactor = clamp(1.0 - factor * factor, 0.0, 1.0);
  return smoothFactor * smoothFactor;
}

float GetDistAtt(float distSqr, float invSqrAttRadius) {
  float attenuation = 1.0 / max(distSqr, 0.01*0.01);
  attenuation *= SmoothDistAtt(distSqr , invSqrAttRadius);
  return attenuation;
}

float GetAngleAtt(vec3 L, vec3 litDir, float litAngleScale, float litAngleOffset) {
  float cd = dot(litDir, L);
  float attenuation = clamp(cd * litAngleScale + litAngleOffset, 0.0, 1.0);
  return (attenuation * attenuation);
}

float GGXMobile(float roughness, float NoH, vec3 H, vec3 N) {
  vec3 NxH = cross(N, H);
  float OneMinusNoHSqr = dot(NxH, NxH);
  float a = roughness * roughness;
  float n = NoH * a;
  float p = a / (OneMinusNoHSqr + n * n);
  return p * p;
}

float CalcSpecular(float roughness, float NoH, vec3 H, vec3 N) {
  return (roughness*0.25 + 0.25) * GGXMobile(roughness, NoH, H, N);
}
vec3 BRDFApprox(vec3 specular, float roughness, float NoV) {
  const vec4 c0 = vec4(-1.0, -0.0275, -0.572, 0.022);
  const vec4 c1 = vec4(1.0, 0.0425, 1.04, -0.04);
  vec4 r = roughness * c0 + c1;
  float a004 = min( r.x * r.x, exp2( -9.28 * NoV ) ) * r.x + r.y;
  vec2 AB = vec2( -1.04, 1.04 ) * a004 + r.zw;
  AB.y *= clamp(50.0 * specular.g, 0.0, 1.0);
  return specular * AB.x + AB.y;
}

vec3 CalcDynamicLighting(vec3 worldPos, vec3 N, vec3 V, vec3 diffuse, vec3 specular, float roughness) {
  vec3 lighting = vec3(0.0);

  vec3 diffuseContrib = diffuse / 3.14159265359;
  for (int i = 0; i < 2; i++) {
    vec3 PLU = cc_sphereLitPos[i].xyz - worldPos;
    vec3 PL = normalize(PLU);
    vec3 PH = normalize(PL + V);
    float PNL = max(dot(N, PL), 0.001);
    float PNH = max(dot(N, PH), 0.0);

    float distSqr = dot(PLU, PLU);
    float litRadius = cc_sphereLitSizeRange[i].x;
    float litRadiusSqr = litRadius * litRadius;
    float illum = 3.14159265359 * (litRadiusSqr / max(litRadiusSqr , distSqr));
    float attRadiusSqrInv = 1.0 / max(cc_sphereLitSizeRange[i].y, 0.01);
    attRadiusSqrInv *= attRadiusSqrInv;
    float att = GetDistAtt(distSqr, attRadiusSqrInv);
    vec3 lspec = specular * CalcSpecular(roughness, PNH, PH, N);
    lighting += PNL * cc_sphereLitColor[i].rgb * cc_sphereLitColor[i].w * illum * att * (diffuseContrib + lspec);
  }
  for (int i = 0; i < 2; i++) {
    vec3 SLU = cc_spotLitPos[i].xyz - worldPos;
    vec3 SL = normalize(SLU);
    vec3 SH = normalize(SL + V);
    float SNL = max(dot(N, SL), 0.001);
    float SNH = max(dot(N, SH), 0.0);

    float distSqr = dot(SLU, SLU);
    float litRadius = cc_spotLitSizeRangeAngle[i].x;
    float litRadiusSqr = litRadius * litRadius;
    float illum = 3.14159265359 * (litRadiusSqr / max(litRadiusSqr , distSqr));
    float attRadiusSqrInv = 1.0 / max(cc_spotLitSizeRangeAngle[i].y, 0.01);
    attRadiusSqrInv *= attRadiusSqrInv;

    float cosInner = max(dot(-cc_spotLitDir[i].xyz, SL), 0.01);
    float cosOuter = cc_spotLitSizeRangeAngle[i].z;
    float litAngleScale = 1.0 / max(0.001, cosInner - cosOuter);
    float litAngleOffset = -cosOuter * litAngleScale;
    float att = GetDistAtt(distSqr, attRadiusSqrInv);
    att *= GetAngleAtt(SL, -cc_spotLitDir[i].xyz, litAngleScale, litAngleOffset);

    vec3 lspec = specular * CalcSpecular(roughness, SNH, SH, N);
    lighting += SNL * cc_spotLitColor[i].rgb * cc_spotLitColor[i].w * illum * att * (diffuseContrib + lspec);
  }

  return lighting;
}

struct StandardSurface {
  vec4 albedo;
  vec3 position;
  vec3 normal;
  vec3 emissive;
  float roughness;
  float metallic;
  float occlusion;
};

vec4 CCStandardShading (StandardSurface s) {
  vec3 diffuse = s.albedo.rgb * (1.0 - s.metallic);
  vec3 specular = mix(vec3(0.04), s.albedo.rgb, s.metallic);

  vec3 N = normalize(s.normal);
  vec3 V = normalize(cc_cameraPos.xyz - s.position);
  vec3 L = normalize(-cc_mainLitDir.xyz);
  vec3 H = normalize(L+V);
  float NV = max(abs(dot(N, V)), 0.001);
  float NL = max(dot(N, L), 0.001);
  float NH = max(dot(N, H), 0.0);

  specular = BRDFApprox(specular, s.roughness, NV);

  vec3 diffuseContrib = diffuse / 3.14159265359;
  vec3 specularContrib = specular * CalcSpecular(s.roughness, NH, H, N);

  vec3 finalColor = NL * cc_mainLitColor.rgb * cc_mainLitColor.w * (diffuseContrib + specularContrib);
  finalColor += CalcDynamicLighting(s.position, N, V, diffuse, specular, s.roughness);

  float fAmb = 0.5 - N.y * 0.5;
  vec3 ambDiff = mix(cc_ambientSky.rgb, cc_ambientGround.rgb, fAmb) * cc_ambientSky.w;
  finalColor += (ambDiff.rgb * diffuse);

  #if CC_USE_IBL
    vec3 R = normalize(reflect(-V, N));

    #ifdef GL_EXT_shader_texture_lod
      vec4 envmap = textureCubeLodEXT(cc_environment, R, s.roughness * cc_ambientGround.w);
    #else
      vec4 envmap = textureCube(cc_environment, R);
    #endif

    #if CC_USE_IBL == 2
      vec3 env = unpackRGBE(envmap);
    #else
      vec3 env = SRGBToLinear(envmap.rgb) * cc_ambientSky.w;
    #endif
    finalColor += env * specular;
  #endif

  finalColor = finalColor * s.occlusion;

  #if CC_USE_HDR
    s.emissive *= cc_exposure.w;
  #endif
  finalColor += s.emissive;

  return vec4(finalColor, s.albedo.a);
}

vec3 ACESToneMap(vec3 color) {
    const float A = 2.51;
    const float B = 0.03;
    const float C = 2.43;
    const float D = 0.59;
    const float E = 0.14;
    return (color * (A * color + B)) / (color * (C * color + D) + E);
}

vec4 CCFragOutput (vec4 color) {
  #if !CC_USE_HDR
    color.rgb = ACESToneMap(color.rgb);
    color.rgb = LinearToSRGB(color.rgb);
  #endif
  return color;
}

uniform vec4 albedo;
uniform vec4 albedoScale;
uniform vec4 pbrParams;
uniform vec4 pbrScale;
uniform vec4 emissive;
uniform vec4 emissiveScale;

varying vec3 v_position;

#if USE_ALBEDO_MAP || USE_NORMAL_MAP || USE_PBR_MAP || USE_EMISSIVE_MAP || USE_METALLIC_ROUGHNESS_MAP || USE_OCCLUSION_MAP
  varying vec2 v_uv;
#endif

#if USE_VERTEX_COLOR
  varying vec3 v_color;
#endif

varying vec3 v_normal;
#if USE_NORMAL_MAP
  varying vec3 v_tangent;
  varying vec3 v_bitangent;
#endif

#if USE_ALBEDO_MAP
  uniform sampler2D albedoMap;
#endif
#if USE_NORMAL_MAP
  uniform sampler2D normalMap;
#endif
#if USE_PBR_MAP
  uniform sampler2D pbrMap;
#endif
#if USE_METALLIC_ROUGHNESS_MAP
  uniform sampler2D metallicRoughnessMap;
#endif
#if USE_OCCLUSION_MAP
  uniform sampler2D occlusionMap;
#endif
#if USE_EMISSIVE_MAP
  uniform sampler2D emissiveMap;
#endif
#if USE_ALPHA_TEST
#endif

void surf (out StandardSurface s) {
  vec4 baseColor = albedo;
  #if USE_VERTEX_COLOR
    baseColor.rgb *= v_color;
  #endif
  #if USE_ALBEDO_MAP
    vec4 texColor = texture2D(albedoMap, v_uv);
    texColor.rgb = SRGBToLinear(texColor.rgb);
    baseColor *= texColor;
  #endif
  s.albedo = baseColor;
  s.albedo.rgb *= albedoScale.xyz;

  #if USE_ALPHA_TEST
    if (s.albedo.ALPHA_TEST_CHANNEL < albedoScale.w) discard;
  #endif

  s.normal = v_normal;
  #if USE_NORMAL_MAP
    vec3 nmmp = texture2D(normalMap, v_uv).xyz - vec3(0.5);
    s.normal =
      (nmmp.x * pbrScale.w) * normalize(v_tangent) +
      (nmmp.y * pbrScale.w) * normalize(v_bitangent) +
      nmmp.z * normalize(s.normal);
  #endif

  s.position = v_position;

  vec4 pbr = pbrParams;
  #if USE_PBR_MAP
    pbr = texture2D(pbrMap, v_uv);
  #endif
  #if USE_METALLIC_ROUGHNESS_MAP
    vec4 metallicRoughness = texture2D(metallicRoughnessMap, v_uv);
    pbr.METALLIC_CHANNEL = metallicRoughness.METALLIC_CHANNEL;
    pbr.ROUGHNESS_CHANNEL = metallicRoughness.ROUGHNESS_CHANNEL;
  #endif
  #if USE_OCCLUSION_MAP
    pbr.OCCLUSION_CHANNEL = texture2D(occlusionMap, v_uv).OCCLUSION_CHANNEL;
  #endif
  pbr *= pbrScale;
  s.roughness = clamp(pbr.ROUGHNESS_CHANNEL, 0.04, 1.0);
  s.metallic = clamp(pbr.METALLIC_CHANNEL, 0.0, 0.96);
  s.occlusion = pbr.OCCLUSION_CHANNEL;

  s.emissive = emissive.rgb * emissiveScale.xyz;
  #if USE_EMISSIVE_MAP
    s.emissive *= SRGBToLinear(texture2D(emissiveMap, v_uv).rgb);
  #endif
}

vec4 frag () {
  StandardSurface s; surf(s);
  vec4 color = CCStandardShading(s);
  return CCFragOutput(color);
}

void main() { gl_FragColor = frag(); }

glsl3.vert:
precision highp float;

vec4 quaternionFromAxisAngle(float angle, vec3 axis){
    angle /= 2.;
    float s = sin(angle);
    vec4 res;
    res.xyz = s * axis;
    res.w = cos(angle);
    return res;
}

vec4 quaternionFromAxis(vec3 xAxis,vec3 yAxis,vec3 zAxis){
    mat3 m = mat3(xAxis,yAxis,zAxis);
    float trace = m[0][0] + m[1][1] + m[2][2];
    vec4 quat;

    if (trace > 0.) {
        float s = 0.5 / sqrt(trace + 1.0);
        quat.w = 0.25 / s;
        quat.x = (m[2][1] - m[1][2]) * s;
        quat.y = (m[0][2] - m[2][0]) * s;
        quat.z = (m[1][0] - m[0][1]) * s;
    } else if ((m[0][0] > m[1][1]) && (m[0][0] > m[2][2])) {
        float s = 2.0 * sqrt(1.0 + m[0][0] - m[1][1] - m[2][2]);
        quat.w = (m[2][1] - m[1][2]) / s;
        quat.x = 0.25 * s;
        quat.y = (m[0][1] + m[1][0]) / s;
        quat.z = (m[0][2] + m[2][0]) / s;
    } else if (m[1][1] > m[2][2]) {
        float s = 2.0 * sqrt(1.0 + m[1][1] - m[0][0] - m[2][2]);
        quat.w = (m[0][2] - m[2][0]) / s;
        quat.x = (m[0][1] + m[1][0]) / s;
        quat.y = 0.25 * s;
        quat.z = (m[1][2] + m[2][1]) / s;
    } else {
        float s = 2.0 * sqrt(1.0 + m[2][2] - m[0][0] - m[1][1]);
        quat.w = (m[1][0] - m[0][1]) / s;
        quat.x = (m[0][2] + m[2][0]) / s;
        quat.y = (m[1][2] + m[2][1]) / s;
        quat.z = 0.25 * s;
    }

    float len = quat.x * quat.x + quat.y * quat.y + quat.z * quat.z + quat.w * quat.w;
    if (len > 0.) {
        len = 1. / sqrt(len);
        quat.x = quat.x * len;
        quat.y = quat.y * len;
        quat.z = quat.z * len;
        quat.w = quat.w * len;
    }

    return quat;
}

vec4 quaternionFromEuler(vec3 angle){
    float x = angle.x / 2.;
    float y = angle.y / 2.;
    float z = angle.z / 2.;

    float sx = sin(x);
    float cx = cos(x);
    float sy = sin(y);
    float cy = cos(y);
    float sz = sin(z);
    float cz = cos(z);

    vec4 quat = vec4(0);

    quat.x = sx * cy * cz + cx * sy * sz;
    quat.y = cx * sy * cz + sx * cy * sz;
    quat.z = cx * cy * sz - sx * sy * cz;
    quat.w = cx * cy * cz - sx * sy * sz;

    return quat;
}

mat4 matrixFromRT(vec4 q, vec3 p){
    float x2 = q.x + q.x;
    float y2 = q.y + q.y;
    float z2 = q.z + q.z;
    float xx = q.x * x2;
    float xy = q.x * y2;
    float xz = q.x * z2;
    float yy = q.y * y2;
    float yz = q.y * z2;
    float zz = q.z * z2;
    float wx = q.w * x2;
    float wy = q.w * y2;
    float wz = q.w * z2;

    return mat4(
        1. - (yy + zz), xy + wz, xz - wy, 0,
        xy - wz, 1. - (xx + zz), yz + wx, 0,
        xz + wy, yz - wx, 1. - (xx + yy), 0,
        p.x, p.y, p.z, 1
    );
}

mat4 matFromRTS(vec4 q, vec3 t, vec3 s){
    float x = q.x, y = q.y, z = q.z, w = q.w;
    float x2 = x + x;
    float y2 = y + y;
    float z2 = z + z;

    float xx = x * x2;
    float xy = x * y2;
    float xz = x * z2;
    float yy = y * y2;
    float yz = y * z2;
    float zz = z * z2;
    float wx = w * x2;
    float wy = w * y2;
    float wz = w * z2;
    float sx = s.x;
    float sy = s.y;
    float sz = s.z;

    return mat4((1. - (yy + zz)) * sx, (xy + wz) * sx, (xz - wy) * sx, 0,
        (xy - wz) * sy, (1. - (xx + zz)) * sy, (yz + wx) * sy, 0,
        (xz + wy) * sz, (yz - wx) * sz, (1. - (xx + yy)) * sz, 0,
        t.x, t.y, t.z, 1);
}

void scaleMatrix(inout mat4 m, float s){
    m[0].xyz *= s;
    m[1].xyz *= s;
    m[2].xyz *= s;
}

vec4 quatMultiply(vec4 a, vec4 b){
    vec4 quat;
    quat.x = a.x * b.w + a.w * b.x + a.y * b.z - a.z * b.y;
    quat.y = a.y * b.w + a.w * b.y + a.z * b.x - a.x * b.z;
    quat.z = a.z * b.w + a.w * b.z + a.x * b.y - a.y * b.x;
    quat.w = a.w * b.w - a.x * b.x - a.y * b.y - a.z * b.z;
    return quat;
}

void rotateVecFromQuat(inout vec3 v, vec4 q){
    float ix = q.w * v.x + q.y * v.z - q.z * v.y;
    float iy = q.w * v.y + q.z * v.x - q.x * v.z;
    float iz = q.w * v.z + q.x * v.y - q.y * v.x;
    float iw = -q.x * v.x - q.y * v.y - q.z * v.z;
    v.x = ix * q.w + iw * -q.x + iy * -q.z - iz * -q.y;
    v.y = iy * q.w + iw * -q.y + iz * -q.x - ix * -q.z;
    v.z = iz * q.w + iw * -q.z + ix * -q.y - iy * -q.x;
}

vec3 rotateVecFromAxis(vec3 v, vec3 axis, float theta){
    return cos(theta) * v + sin(theta) * cross(v, axis) + (1. - cos(theta)) * dot(v, axis) * axis;
}

vec3 rotateInLocalSpace(vec3 pos, vec3 xAxis, vec3 yAxis, vec3 zAxis, vec4 q){
    vec4 viewQuat = quaternionFromAxis(xAxis, yAxis, zAxis);
    vec4 rotQuat = quatMultiply(viewQuat, q);
    rotateVecFromQuat(pos, rotQuat);
    return pos;
}

void rotateCorner(inout vec2 corner, float angle){
    float xOS = cos(angle) * corner.x - sin(angle) * corner.y;
    float yOS = sin(angle) * corner.x + cos(angle) * corner.y;
    corner.x = xOS;
    corner.y = yOS;
}

uniform Constants{
    vec4 mainTiling_Offset;
    vec4 frameTile_velLenScale;
    vec4 scale;
};
uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};
uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
};

out vec2 uv;
out vec4 color;

void computeVertPos(inout vec4 pos, vec2 vertOffset, vec4 q, vec3 s
#if CC_USE_BILLBOARD || CC_USE_VERTICAL_BILLBOARD
    , mat4 viewInv
