uniform CCGlobal {
  vec4 cc_time;
  vec4 cc_screenSize;
  vec4 cc_screenScale;
  vec4 cc_nativeSize;
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_exposure;
  vec4 cc_mainLitDir;
  vec4 cc_mainLitColor;
